//program to demonstrate on nested for loop
package looping;

public class NestedForloopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//print star square pattern
		for(int i = 1; i<=4; i++) {
			for(int j = 1; j<=4; j++) {
				System.out.print("* ");
			}
			System.out.println();
		}

	}

}
